import './App.css'
import React, { Component } from "react";
import { CardColumns, Card, Button, ListGroup } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import DeleteIcon from '@material-ui/icons/Delete';
import { ListItemIcon } from '@material-ui/core';
import { StayPrimaryPortraitSharp } from '@material-ui/icons';

export default class Playlist extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
        };
    }

    playlistCard = item => {
        return (
            <div className="playlistCard">
                <div className = "imageWrapper">
                <img src={item.source} alt={item.title} />
                </div>
                <ListGroup.Item>
                <Card>
                    <Card.Body>
                        <Card.Title>{this.props.song.title}</Card.Title>
                        <Card.Text>
                            Artist: {this.props.song.artist}
                        </Card.Text>
                        <Card.Text>
                            Language: {this.props.song.language}
                        </Card.Text>
                        <Card.Text>
                            Singer: {this.props.song.singer}
                        </Card.Text>
                        <Button variant="primary" onClick={() => this.addSong()}>
                            Delete Song</Button>
                        <ListItemIcon>
                            <div className = "IconWrap"><DeleteIcon /></div>
                            )
                        </ListItemIcon>
                    </Card.Body>
                    </Card>
                    </ListGroup.Item>
            </div>
        )
    }

    render() {
        return (
            <div>
                <ListGroup>
                    {(this.props.list.length > 0) ? this.props.list.map(item => { return this.playlistCard(item) }) : "No Songs in Playlist"}
                </ListGroup>
            </div>
        )
    }
}
