import './App.css'
import React, { Component } from "react";
import { CardDeck, Card } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

export default class DisplayList extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
        };

    }

    makeCard = item => {
        return (
            <Card>
                <Card.Img variant="top" src="holder.js/200px200" />
                <Card.Body>
                    <Card.Title>{item.title}</Card.Title>
                    <Card.Text>
                        Artist: {item.artist}
                        Language: {item.language}
                        Singer: {item.singer}
                        Duration: {Math.floor(item.duration / 60)}:{item.duration % 60}
      </Card.Text>
                </Card.Body>
                <Card.Footer>
                    <small className="text-muted">Last updated 3 mins ago</small>
                </Card.Footer>
                <li>{item.title} by {item.artist} [{Math.floor(item.duration / 60)}:{item.duration % 60}]</li>
            </Card>
        )
    }

    render() {
        return (
            <div>
                <CardDeck>
                    {this.props.list.map(item => { return this.makeCard(item) })}
                </CardDeck>
            </div>
        )
    }
}
