import './App.css'
import React, { Component } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';

export default class DisplayList extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            sort: "Default"
        };

    }

    onSortList = event => {
        this.setState(
            { sort: event }
        );
    }

    render() {
        return (
            <div>
                {this.props.list.map(item =>
                    <li>{item.title}, {item.artist}</li>)}
            </div>
        )
    }
}
